mapper.insert:
insert
  into 
       dday
      (
       dday_no
     , member_no
     , dday_title
     , dday_date
      )
values
      (
        seq_ddayNo.nextval
      , ${memberNo}
      , #{ddayTitle}
      , #{ddayDate}
      )

mapper.update:
update 
       dday
   set 
        dday_title = #{ddayTitle}
      , dday_date = #{ddayDate}
  where dday_no = #{ddayNo}
