mapper.insert:
insert
  into diary
  (
  	 diary_no
   , diary_writer
   , diary_title
   , diary_content
  )
  values
  (
     seq_diaryno.nextval
   , #{diaryWriter}
   , #{diaryTitle}
   , #{diaryContent}
    )

mapper.update:
update
		diary
   set diary_title = #{diaryTitle}
     , diary_content = #{diaryContent}
     , diary_date = sysdate
 where diary_no = #{diaryNo}
