mapper.insert:
insert
  into freenote
  (
     freenote_no
   , freenote_title
   , freenote_content
   , freenote_category
   , freenote_topic
   , freenote_privacy
   , freenote_privacy_comm
   , freenote_comment_yn
   , member_no
  )
  values
  (
     seq_freenoteno.nextval
   , #{freenoteTitle}
   , #{freenoteContent}
   , #{freenoteCategory}
   , #{freenoteTopic}
   , #{freenotePrivacy}
   , #{freenotePrivacyComm}
   , #{freenoteCommentYN}
   , #{freenoteWriter}
  )

mapper.update:
update
       freenote
   set freenote_title = #{freenoteTitle}
     , freenote_content = #{freenoteContent}
     , freenote_category = #{freenoteCategory}
     , freenote_topic = #{freenoteTopic}
     , freenote_privacy = #{freenotePrivacy}
     , freenote_privacy_comm = #{freenotePrivacyComm}
     , freenote_comment_yn = #{freenoteCommentYN}
     , modify_date = sysdate
 where freenote_no = #{freenoteNo}

mapper.insert:
insert
  into reply
  (
     reply_no
   , reply_content
   , ref_fno
   , member_no

   , ref_rno

   , reply_level
  )
  values
  (
     seq_replyno.nextval
   , #{replyContent}
   , #{refFno}
   , #{replyWriter}

   , #{refRno}

   , #{replyLevel}
  )
