mapper.insert:
insert
        into inquiry
        (
            inquiry_no
            , inquiry_title
            , inquiry_writer
            , inquiry_content
            , inquiry_type 
            , inquiry_email

        )
        values
        (
            SEQ_INQUIRYNO.nextval
            , #{inquiryTitle}
            , #{inquiryWriter}
            , #{inquiryContent}
            , #{inquiryType}
    		, #{inquiryEmail}
        )

