mapper.update:
update memo
   set memo_widget = 'Y'
 where memo_no = #{memoNo}

update memo
   set memo_widget = 'N'
 where memo_writer = #{memoWriter}
   and memo_no != #{memoNo}

update memo
   set memo_widget = 'N'
 where memo_writer = #{memoWriter}

update memo
   set 
       memo_title = #{memoTitle},
       memo_content = #{memoContent}
 where memo_no = #{memoNo}
